import { useReducer } from "react/cjs/react.development";
import CartContext from "./cart-context";

const defaultCartState = {
    items: [],
    totalAmount: 0
}

const cartReducer = (state, action) => {
    if(action.type === "ADDING_ITEM") {
        const updatedTotal = state.totalAmount + (action.item.price * action.item.amount)

        const existInItems = state.items.findIndex((item) => item.id === action.item.id);

        const oldItem = state.items[existInItems];
        let updatedItems;
        if(existInItems) {
            let updateItem = {
                ...oldItem,
                amount: oldItem.amount + action.items.amount
            }
            updatedItems = [...state.items];
            updatedItems[existInItems] = updateItem;
        } else {
            updatedItems = state.items.concat(action.item);
        }


        return {
            items: updatedItems,
            totalAmount: updatedTotal
        }
    }
    if(action.type === "UPDATE_AMOUNT")

    return defaultCartState;
}

const CartContextProvider = ({ children }) => {

    const [Cartstate, dispatchCartAction] = useReducer(cartReducer,defaultCartState)

    const addItemHandler = (item) => {
        dispatchCartAction({type: 'ADDING_ITEM', item: item });
    }

    const removeItemHandler = (id) => {
        dispatchCartAction({type: 'REMOVE_ITEM', id: id});
    }
    const cartContext = {
        items: Cartstate.items,
        totalAmount: Cartstate.totalAmount,
        addItem: addItemHandler,
        removeItem: removeItemHandler,
    };

  return(
    <CartContext.Provider value={cartContext}>
        {children}
    </CartContext.Provider>
    ); 
};

export default CartContextProvider;
