import React from 'react'
import Input from '../../UI/Input/Input'
import classes from './MealItemForm.module.css';

const MealItemForm = ({id}) => {

    return (
        <form className={classes.form}>
            <Input 
                content="Amount"
                id={'amount'+id}
                input={{
                    type: 'number',
                    min: '1',
                    max: '5',
                    step: '1',
                    defaultValue: '1'
                }}
            />
            <button>Add to cart</button>
        </form>
    );
}

export default MealItemForm;
