import Card from "../UI/Card/Card";

const MealItem = ({mealName, description, price}) => {
    return (
        <Card>
            <li>
                <h3>{mealName}</h3>
                <span>
                    {description}
                </span>
            </li>
        </Card>
    );
}

export default MealItem;
